ctProperties属性来打开mergeSql功能；慢SQL记录\r\n      connection-properties: druid.stat.mergeSql=true;druid.stat.slowSqlMillis=5000\r\n      \r\ndruid:\r\n  config:\r\n    #是否开启druid监控com.silankeji.mybatisplus\r\n    enable: true\r\n    #白名单 默认所有可访问\r\n    allow: 127.0.0.1\r\n    #黑名单 默认为空\r\n    #deny: ip\r\n    #账号 默认admin\r\n    loginUsername: wsw\r\n    #密码 默认admin123\r\n    loginPassword: wsw-979\r\n    #是否允许点击页面的重置按钮，默认false\r\n    resetEnable: false\r\n    #过滤url 默认 /*\r\n    urlPatterns: /*\r\n    #不过滤的资源,注意*打头必须加双引号，下面值为默认值\r\n    exclusions: \"*.js,*.gif,*.jpg,*.png ,*.css,*.ico,/druid/*\"\r\n\r\n#mybatis-plus\r\nmybatis-plus:\r\n  mapper-locations: classpath:/mapper/*Mapper.xml\r\n  type-aliases-package: io.cloud.api.entity\r\n  global-config:\r\n    #uuid\r\n    id-type: 3\r\n    #数据中心id\r\n    datacenter-id: 0\r\n    worker-id: 1\r\n    #刷新mapper 调试神器\r\n    refresh-mapper: true\r\n    #驼峰下划线转换\r\n    db-column-underline: true\r\n    banner: false\r\n  configuration:\r\n    #配置返回数据库(column下划线命名&&返回java实体是驼峰命名)\r\n    #自动匹配无需as（没开启这个，SQL需要写as： select user_id as userId）\r\n    map-underscore-to-camel-case: true\r\n    cache-enabled: false\r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \'*\'\r\n  endpoint:\r\n    health:\r\n      show-details: always\r\n\r\n#日志\r\nlogging:\r\n  path: classpath*:logback-spring.xml\r\n  #心跳级别提升至 warn \r\n  level:\r\n    com:\r\n      alibaba:\r\n        nacos:\r\n          client:\r\n            naming: warn\r\n\r\n# feign 配置\r\nfeign:\r\n  hystrix:\r\n    enabled: true\r\n  okhttp:\r\n    enabled: true\r\n  httpclient:\r\n    enabled: false\r\n  client:\r\n    config:\r\n      default:\r\n        connectTimeout: 10000\r\n        readTimeout: 10000\r\n  compression:\r\n    request:\r\n      enabled: true\r\n    response:\r\n      enabled: true\r\n\r\n# hystrix 配置\r\nhystrix:\r\n  command:\r\n    default:\r\n      execution:\r\n        isolation:\r\n          strategy: SEMAPHORE\r\n          thread:\r\n            timeoutInMilliseconds: 60000\r\n  shareSecurityContext: true\r\n\r\n#请求处理的超时时间\r\nribbon:\r\n  ReadTimeout: 10000\r\n  ConnectTimeout: 10000\r\n  \r\n', '3904abd0a6b4150be4748879628029b5', '2010-05-05 00:00:00', '2020-06-03 06:05:32', NULL, '0:0:0:0:0:0:0:1', 'U', '');
