enabled: true\r\n\r\n# hystrix 配置\r\nhystrix:\r\n  command:\r\n    fallbackcmd:\r\n      fallback:\r\n        enabled: true\r\n        isolation:\r\n          semaphore:\r\n            maxConcurrentRequests: 10\r\n      execution:\r\n        timeout:\r\n          enabled: true\r\n        isolation:\r\n          strategy: SEMAPHORE\r\n          thread:\r\n            timeoutInMilliseconds: 5000\r\n            interruptOnTimeout: true\r\n            interruptOnFutureCancel: false\r\n  shareSecurityContext: true\r\n\r\n#请求处理的超时时间\r\nribbon:\r\n  ReadTimeout: 5000\r\n  ConnectTimeout: 3000', '6a6fc0526e4a3684f679d31fc0ec8cca', '2010-05-05 00:00:00', '2020-06-04 12:57:16', NULL, '0:0:0:0:0:0:0:1', 'U', '');
