on/xml,application/json\r\n      #设置触发压缩的大小下限\r\n      min-request-size: 2048\r\n    response:\r\n      enabled: true\r\n\r\n# hystrix 配置\r\nhystrix:\r\n  shareSecurityContext: true\r\n  command:\r\n    fallbackcmd:\r\n      execution:\r\n        timeout:\r\n          enabled: true\r\n        isolation:\r\n          strategy: SEMAPHORE\r\n          thread:\r\n            timeoutInMilliseconds: 5000\r\n\r\n\r\n#请求处理的超时时间\r\nribbon:\r\n  eager-load:\r\n    #开启饥饿加载 解决第一次feign调用失败的问题\r\n    enabled: true             \r\n  ReadTimeout: 5000\r\n  ConnectTimeout: 4000\r\n\r\nwsw:\r\n  ribbon:\r\n    isolation:\r\n      enabled: true', '60d28a6550fb4ab6a045cd3fff11bd64', '2010-05-05 00:00:00', '2020-06-11 06:12:38', NULL, '0:0:0:0:0:0:0:1', 'U', '');
