INSERT INTO `his_config_info` VALUES (16, 255, 'application.yml', 'DEFAULT_GROUP', '', 'spring:\r\n  mvc:\r\n    servlet:\r\n      #启动时立即初始化 dispatcherServlet，数值表示延迟多久进行自动初始化dispatcherServlet，0是立即初始化\r\n      load-on-startup: 0\r\n  aop:\r\n    proxy-target-class: true\r\n  autoconfigure: \r\n    exclude: com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure\r\n  #redis 配置\r\n  redis:\r\n    #ip\r\n    host: localhost\r\n    #密码\r\n    password:\r\n    #端口\r\n    port: 6379\r\n    #客户端超时时间单位是毫秒 默认是2000\r\n    timeout: 10000\r\n    lettuce:\r\n      pool:\r\n        #最大空闲数\r\n        maxIdle: 300\r\n        #连接池的最大数据库连接数。设为0表示无限制,如果是jedis 2.4以后用redis.maxTotal\r\n        #redis.maxActive=600\r\n        #控制一个pool可分配多少个jedis实例,用来替换上面的redis.maxActive,如果是jedis 2.4以后用该属性\r\n        maxTotal: 1000\r\n        #最大建立连接等待时间。如果超过此时间将接到异常。设为-1表示无限制。\r\n        maxWaitMillis: 1000\r\n        #连接的最小空闲时间 默认1800000毫秒(30分钟)\r\n        minEvictableIdleTimeMillis: 300000\r\n        #每次释放连接的最大数目,默认3\r\n        numTestsPerEvictionRun: 1024\r\n        #逐出扫描的时间间隔(毫秒) 如果为负数,则不运行逐出线程, 默认-1\r\n        timeBetweenEvictionRunsMillis: 30000\r\n        #是否在从池中取出连接前进行检验,如果检验失败,则从池中去除连接并尝试取出另一个\r\n        testOnBorrow: true\r\n        #在空闲时检查有效性, 默认false\r\n        testWhileIdle: true\r\n\r\n  # 数据源基础配置\r\n  datasource:\r\n     dynamic:\r\n        datasource:\r\n          #druid\r\n          druid:\r\n            #以下均为默认值\r\n            #数据源配置 wall表示防火墙\r\n            filters: stat,wall,log4j2\r\n            #初始线程数，最小活跃线程，最大活跃线程\r\n            initial-size: 1\r\n            min-idle: 1\r\n            max-active: 5\r\n            # 连接等待时间单位毫秒\r\n            max-wait: 60000\r\n            #配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\r\n            time-between-eviction-runs-millis: 60000\r\n            #配置一个连接在池中最小生存的时间，单位是毫秒\r\n            min-evictable-idle-time-millis: 300000\r\n            validation-query: SELECT 1 FROM DUAL\r\n            test-while-idle: true\r\n            test-on-borrow: false\r\n            test-on-return: false\r\n            #打开PSCache，并且指定每个连接上PSCache的大小\r\n            pool-prepared-statements: false\r\n            max-pool-prepared-statement-per-connection-size: 20\r\n            #通过connectProperties属性来打开mergeSql功能；慢SQL记录\r\n            connection-properties: druid.stat.mergeSql=true;druid.stat.slowSqlMillis=5000\r\n\r\ndruid:\r\n  config:\r\n    #是否开启druid监控com.silankeji.mybatisplus\r\n    enable: true\r\n    #白名单 默认所有可访问\r\n    allow: 127.0.0.1\r\n    #黑名单 默认为空\r\n    #deny: ip\r\n    #账号 默认admin\r\n    loginUsername: wsw\r\n    #密码 默认admin123\r\n    loginPassword: wsw-979\r\n    #是否允许点击页面的重置按钮，默认false\r\n    resetEnable: false\r\n    #过滤url 默认 /*\r\n    urlPatterns: /*\r\n    #不过滤的资源,注意*打头必须加双引号，下面值为默认值\r\n    exclusions: \"*.js,*.gif,*.jpg,*.png ,*.css,*.ico,/druid/*\"\r\n\r\n#mybatis-plus\r\nmybatis-plus:\r\n  mapper-locations: classpath:/mapper/*Mapper.xml\r\n  type-aliases-package: io.cloud.api.entity\r\n  global-config:\r\n    #uuid\r\n    id-type: 3\r\n    #数据中心id\r\n    datacenterId: 18\r\n    #机器 ID 部分(影响雪花ID)\r\n    workerId: 3\r\n    #刷新mapper 调试神器\r\n    refresh-mapper: true\r\n    #驼峰下划线转换\r\n    db-column-underline: true\r\n    banner: false\r\n  configuration:\r\n    #配置返回数据库(column下划线命名&&返回java实体是驼峰命名)\r\n    #自动匹配无需as（没开启这个，SQL需要写as： select user_id as userId）\r\n    map-underscore-to-camel-case: true\r\n    cache-enabled: false\r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \'*\'\r\n  endpoint:\r\n    health:\r\n      show-details: always\r\n\r\n#日志\r\nlogging:\r\n  path: classpath*:logback-spring.xml\r\n  #心跳级别提升至 warn\r\n  level:\r\n    com:\r\n      alibaba:\r\n        nacos:\r\n          client:\r\n            naming: warn\r\n\r\n# feign 配置\r\nfeign:\r\n  # hystrix:\r\n    # enabled: true\r\n  sentinel:\r\n    enabled: true\r\n  okhttp:\r\n    enabled: true\r\n  httpclient:\r\n    enabled: false\r\n  client:\r\n    config:\r\n      default:\r\n        connectTimeout: 5000\r\n        readTimeout: 5000\r\n  compression:\r\n    request:\r\n      #开启请求压缩\r\n      enabled: true\r\n      #设置压缩的数据类型\r\n      mime-types: text/html,application/xml,application/json\r\n      #设置触发压缩的大小下限\r\n      min-request-size: 2048\r\n    response:\r\n      enabled: true\r\n\r\n# hystrix 配置\r\n# hystrix:\r\n#   shareSecurityContext: true\r\n#   command:\r\n#     fallbackcmd:\r\n#       execution:\r\n#         timeout:\r\n#           enabled: true\r\n#         isolation:\r\n#           strategy: SEMAPHORE\r\n#           thread:\r\n#             timeoutInMilliseconds: 5000\r\n\r\n\r\n#请求处理的超时时间\r\nribbon:\r\n  eager-load:\r\n    #开启饥饿加载 解决第一次feign调用失败的问题\r\n    enabled: true             \r\n  ReadTimeout: 5000\r\n  ConnectTimeout: 4000\r\n\r\nwsw:\r\n  ribbon:\r\n    isolation:\r\n      enabled: true', 'aa5c8bf0f6a7420a4ea1e1d43bde5837', '2010-05-05 00:00:00', '2020-06-11 14:41:11', NULL, '0:0:0:0:0:0:0:1', 'U', '');
