INSERT INTO `his_config_info` VALUES (16, 302, 'application.yml', 'DEFAULT_GROUP', '', 'spring:\r\n  mvc:\r\n    servlet:\r\n      #启动时立即初始化 dispatcherServlet，数值表示延迟多久进行自动初始化dispatcherServlet，0是立即初始化\r\n      load-on-startup: 0\r\n  aop:\r\n    proxy-target-class: true\r\n  autoconfigure: \r\n    exclude: com.alibaba.druid.spring.boot.autoconfigure.DruidDataSourceAutoConfigure\r\n\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \'*\'\r\n  endpoint:\r\n    health:\r\n      show-details: always\r\n\r\n#日志\r\nlogging:\r\n  path: classpath*:logback-spring.xml\r\n  #心跳级别提升至 warn\r\n  level:\r\n    com:\r\n      alibaba:\r\n        nacos:\r\n          client:\r\n            naming: warn\r\n\r\n# feign 配置\r\nfeign:\r\n  # hystrix:\r\n    # enabled: true\r\n  sentinel:\r\n    enabled: true\r\n  okhttp:\r\n    enabled: true\r\n  httpclient:\r\n    enabled: false\r\n  client:\r\n    config:\r\n      default:\r\n        connectTimeout: 5000\r\n        readTimeout: 5000\r\n  compression:\r\n    request:\r\n      #开启请求压缩\r\n      enabled: true\r\n      #设置压缩的数据类型\r\n      mime-types: text/html,application/xml,application/json\r\n      #设置触发压缩的大小下限\r\n      min-request-size: 2048\r\n    response:\r\n      enabled: true\r\n\r\n# hystrix 配置\r\n# hystrix:\r\n#   shareSecurityContext: true\r\n#   command:\r\n#     fallbackcmd:\r\n#       execution:\r\n#         timeout:\r\n#           enabled: true\r\n#         isolation:\r\n#           strategy: SEMAPHORE\r\n#           thread:\r\n#             timeoutInMilliseconds: 5000\r\n\r\n\r\n#请求处理的超时时间\r\nribbon:\r\n  eager-load:\r\n    #开启饥饿加载 解决第一次feign调用失败的问题\r\n    enabled: true             \r\n  ReadTimeout: 5000\r\n  ConnectTimeout: 4000\r\n\r\nwsw:\r\n  ribbon:\r\n    isolation:\r\n      enabled: true', '590167a1ac364d41121cf7322395c87b', '2010-05-05 00:00:00', '2020-06-13 02:29:45', NULL, '0:0:0:0:0:0:0:1', 'U', '');
